import React from 'react';
import './index.css';

const Card = ({ id, name, email}) => {
	return(
		<div className="card bg-light-green dib br3 pa3 ma2 shadow3 grow">
			<img alt='robots' src={`https://robohash.org/${id}?100x100`}/>
			<div className="tc pa1 ma6">
				<h1>{ name }</h1>
				<p>{ email }</p>
			</div>
		</div>
	);
}

export default Card;
